function SingleloadMorePhotos(MoreButton,loadMoreNonce,galleryContainer,currentPhotoId){const categoryIds=JSON.parse(MoreButton.getAttribute('data-category-ids'));fetch(MoreButton.getAttribute('data-ajaxurl'),{method:'POST',headers:{'Content-Type':'application/x-www-form-urlencoded',},body:new URLSearchParams({action:'single_load_more_photos',category_ids:JSON.stringify(categoryIds),load_more_nonce:loadMoreNonce,current_photo_id:currentPhotoId}),}).then(response=>response.text()).then(html=>{galleryContainer.innerHTML=html;initializeThickbox();});}
$(document).ready(function(){var page=1;var foundPosts=$('#load-more-button').data('found-posts');function FrontloadMorePhotos(){const ajaxUrl=$('#load-more-button').data('ajaxurl');const data={action:'load_more_photos',page:page,category_filter:$('#category-filter').val(),format_filter:$('#format-filter').val(),sort_by:$('#sort-by').val(),};$.ajax({url:ajaxUrl,type:'GET',data:data,success:function(response){if(page===1){$('.container').empty();}
$('.container').append(response);page++;if($('.img-detail').length<foundPosts){$('#load-more-button').show();}else{$('#load-more-button').hide();}
initializeThickbox();}});}
$('#load-more-button').on('click',function(event){event.preventDefault();FrontloadMorePhotos();});$('#category-filter, #format-filter, #sort-by').on('change',function(){page=1;FrontloadMorePhotos();});});